mapper.select:
SELECT * FROM user_info


		WHERE uemail = #{key}


		WHERE phone_number = #{key}


		WHERE uname = #{key}

mapper.update:
update user_info
uname=#{uname}, user_introduce=#{userIntroduce}, follow_accept=#{followAccept}, real_name=#{realName}, phone_number=#{phoneNumber},
where uname = #{stdname}
